#include "stm32f4xx.h"                  // Device header
#include "delay.h"
#include "LCD.h"
#include "MyDAC.h"
uint8_t yin[] = "";
int main()
{
    board_init();
    LCD_Init();
    MyDAC_Init();
    while(1)
    {
    for(uint32_t i = 0; i < 0x10000; i++)
    {
        Dac1_Set_Vol((uint8_t)yin[i]);
        delay_us(9000);
    }
    }
}
